def findProtein(chromosome, protein):
    for i in range(len(chromosome) - (len(protein) - 1)):
        similar = 0
        if chromosome[i] == protein[0]:
            for j in range(0, len(protein) - 1):
                if chromosome[i + j] == protein[j]:
                    similar = similar + 1
            if similar == len(protein) - 1:
                return(True,i)
            else:
                pass

