def posRNAParts(protein, dict):
    protein = protein + 'Z'
    keys = list(dict.keys())
    values = list(dict.values())
    possibles = []
    for c in protein:
        possible = findRNA(keys, values, c)
        possibles.append(possible)
    print(possibles)

