def rnaToProtein(rna,dict):
    codons = []
    proteins = []
    for i in range(0, len(rna)/3):
        codon = rna[i*3:(i*3)+3]
        codons.append(codon)
    for cod in range(len(codons)):
        protein = translations[codons[cod]]
        proteins.append(protein)
    return proteins

